import React from "react";

function Slider(props) {
  const {images, currentIndex, goToNextSlide, goToPrevSlide} = props
  const sliderStyles = {
    background: `url(${images[currentIndex]})`,
    backgroundSize: "cover",
    backgroundPosition: "25% 85%",
    backgroundRepeat: "no-repeat"
  }
  return (
    <div class="slider">
      <button className="slider__next" onClick= {goToNextSlide}>
        &#10095;
      </button>
      <button className="slider__prev" onClick= {goToPrevSlide}>
        &#10094;
      </button>
      <ul class="slider__slides" >
    <li style={sliderStyles}>Plaid Me</li>
        {/* {images.map(imageUrl => { */}
          {/* return <li style={{background: `url(${imageUrl})`}}>Plaid Me</li> */}
        {/* })} */}
      </ul>
    </div>
  );
}

export default Slider
